import Data.List
import System.IO

liou :: [Int]
liou = liou2 1 

liouExtra :: Int -> [Int] -> [Int]
liouExta 2 xs  = xs 
liouExtra n xs = liouExtra (n+1) (xs ++ (take (factorial (n + 1)) [0,0..]) ++ [1])

liou2 :: Int -> [Int]
liou2 1 = [1] ++ liou2 2
liou2 0 = []
liou2 n = (take ((factorial n) - (factorial (n - 1)) - 1) (repeat 0)) ++ [1] ++ liou2 (n + 1)

factorial :: Int -> Int
factorial 1 = 1
factorial n = n * factorial (n - 1)



duplicate :: Int -> [a] -> [a]
duplicate 1 xs = xs 
duplicate n xs = xs ++ (duplicate (n-1) xs)

five :: Integer
five = 5

